let openPopup=document.querySelectorAll(".openPopup");for(let e=0;e<openPopup.length;e++)openPopup[e].addEventListener("click",function(e){e.preventDefault();let t=e.target.dataset.popup,o=document.getElementById(t).innerHTML;document.getElementById("popupContentBox").innerHTML=o,document.body.classList.add("popup-active")});let closePopup=document.getElementById("closePopup");closePopup.addEventListener("click",function(e){e.preventDefault(),document.body.classList.remove("popup-active")});